import React from 'react';
import {} from "reactstrap";
import background from "../images/bg01.png";
import '../../src/index.css';
import applestore from "../images/applestore.png";
import googleplay from '../images/googleplay.png';
import { useTranslation } from 'react-i18next';



const styles ={

     pagebackground: {
        backgroundImage: `url(${background})`,
        maxHeight:'100vh',
        marginTop:'-90px',
        fontSize:'50px',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
    },

    maintitle: {
        color: "#d13669",
        textAlign: "center",
        marginTop: '20vh',
        fontSize: '75px',
        fontWeight: '500',
        padding: '10px',
    },

    tagline: {
        color: "#342176",
        textAlign: "center",
        fontSize: '25px',
        fontWeight: '500',
        padding: '10px',
    },

    text: {
        color: '#6d757d',
        textAlign: "center",
        fontSize: '15px',
        fontWeight: '400',
        padding: '10px',
    },

    images: {
        align: 'center',
    }

}

const CallToAction = (props) => {
    const { t, i18n } = useTranslation();



    return (
      <div style={styles.pagebackground}>
        {/* <h1 style={styles.maintitle}>Safe Circle</h1>
        <h3 style={styles.tagline}>The safety check app that respects your privacy</h3>
        <p style={styles.text}>The best person to keep your loved ones safe is you.
            <br/>Get notified if your loved one didn&#39;t mark as safe on the scheduled time.</p> */}

            <div class="container">


            <div class="row justify-content-md-center">
                <div class="col col-lg-6">
                    <h1 style={styles.maintitle}>Safe Circle</h1>
                </div>
            </div>
            <div class="row justify-content-center">
                <div class="col-md-auto">
                <h3 style={styles.tagline}>{t('home.footer_text')}</h3>
                </div>
            </div>
                <div class="row justify-content-center">
                <div class="col col-lg-6">
                <p style={styles.text}>The best person to keep your loved ones safe is you.
            <br/>Get notified if your loved one didn&#39;t mark as safe on the scheduled time.</p>
                    </div>

                </div>

                <div class="row justify-content-center">
                    <div class="col col-lg-6">
                    &nbsp;
                    &nbsp;
                    </div> 
                </div>


                <div class="row justify-content-md-center">
                    <div class="col col-lg-2">
                    <img src={applestore} height={60} width={200} />
                    </div>
                    <div class="col col-lg-1">

                    </div>
                    <div class="col col-lg-2">
                    <img src={googleplay} height={60} width={200} />
                    </div>
                </div> 

                <div class="row justify-content-center">
                    <div class="col col-lg-6">
                    &nbsp;
                    &nbsp;
                    &nbsp;
                    &nbsp;
                    </div> 
                </div>

            </div>


        <div style={styles.images}>



        </div>

      </div>

    );
    };


export default CallToAction;
