import React from 'react';
import { useTranslation } from 'react-i18next';
import '../../styles/style.css';
import { useState } from "react";
//import GlobeSvg from "./Globe.svg";
export default function App() {
  const [language, setLanguage] = useState({ value: "en", name: "Anglais" });
  const [dropdown, setDropdown] = useState(false);


  return (
    <div className="App">
      <div className="language-switcher-container">
        {/* <img src={GlobeSvg} alt="globe" /> */}
        <input
          type="button"
          onClick={() => setDropdown(!dropdown)}
          value={language.name}
        />
        <span
          className={dropdown ? "dropdown_icon rotate" : "dropdown_icon"}
        ></span>
      </div>
      <ul className={dropdown ? "show-dropdown" : "hide-dropdown"}>
        <li
          value="en"
          onClick={(e) => {
            setLanguage({ value: "en", name: e.target.innerText });
            setDropdown(!dropdown);
          }}
        >
          Anglais
        </li>
        <li
          value="fr"
          onClick={(e) => {
            setLanguage({ value: "fr", name: e.target.innerText });
            setDropdown(!dropdown);
          }}
        >
          French
        </li>
      </ul>
    </div>
  );
}


