import React from 'react';
import Logo from '../../images/Logo.png';
import { useTranslation } from 'react-i18next';
import '../../App.css';
import { BrowserRouter as Router, Link, Route, Routes } from "react-router-dom";
import Terms from '../Legal/Terms';
import Privacy from '../Legal/PrivacyPolicy';



const Footer = () => {
    const { t } = useTranslation();

    return(
        <div className='footerContainer'>

            <div className='footerlogo'>
            <img src={Logo}  alt='Safety check circle privacy thumbnail'/>
            </div>

            <div className="footerlinks">
                <a href="mailto:iinfo@safecircleapp.com">info@safecircleapp.com</a>
                <Router> 
                    <Link  to="components/Legal/Terms" > {t('footer.privacy')} </Link>
                    <Link  to="components/Legal/PrivacyPolicy" >{t('footer.terms')}</Link>
                        <Routes>
                            <Route path="/components/Legal/Terms" component={Terms} />
                            <Route path="/components/Legal/PrivacyPolicy" component={Privacy} />
                        </Routes>
                </Router>

            </div>

        </div>
    )
}

export default Footer;
