import React from 'react';
import {} from "reactstrap";
import { useTranslation } from 'react-i18next';


const styles = {

    container: {
        width: '30vw',
        padding: '20px',
        background: '#FFFFFF',
    },

    textcontainer:{
        width: '25vw',
        padding: '20px',
    },

    h1: {
        fontSize: '20px',
        color: '#37207b',
    },

    p: {
        fontSize: '14px',
        color: '#404040',
    }
}

const Featurescard = (props) => { 
    const { t } = useTranslation();

return (
    <div style={styles.container}>
        <div style={styles.textcontainer}>
            <h1 style={styles.h1}>{t('features.journeyHeader')}</h1>

            <p style={styles.p}>Have a friend or a family member to be aware of your whereabouts with Safe Circle App. 
                If for some reason you cant mark as safe, everyone in your Circle will be notified that you 
                might be in need of help.</p>
        </div>
    </div>
);

};

export default Featurescard;
